// Loads Azure DevOps SSH Known Hosts
//
//    ssh-keyscan ssh.dev.azure.com

def call () {
  sshKnownHosts("""
# ssh.dev.azure.com:22 SSH-2.0-SSHBlackbox.10
ssh.dev.azure.com ssh-rsa 
# ssh.dev.azure.com:22 SSH-2.0-SSHBlackbox.10
# ssh.dev.azure.com:22 SSH-2.0-SSHBlackbox.10
""", 'Azure DevOps'
  )
}
